import React, { useState } from 'react';
import { ethers } from 'ethers';
import { create } from 'ipfs-http-client';

const client = create({
  host: '127.0.0.1',
  port: 5001,
  protocol: 'http',
});

function ViewComponent() {
  const [userAddress, setUserAddress] = useState('');
  const [fileUrl, setFileUrl] = useState(null);
  //const [recipientAddress, setRecipientAddress] = useState('');

  const CONTRACT_ADDRESS = '0x56543E1324a0E83Dfa6FF44F45a1593aFFb6Cd0f';
  const PRIVATE_KEY = 'ffce87da659a88c5ad63d447fff3be4308b00845bf002861ba91ad0ce5994603';
  const API_KEY = 'rdO0i4o--THH81mB5V3Cb9SE1UiwCtrh';

  const network = 'maticmum';
  const provider = new ethers.providers.AlchemyProvider(network, API_KEY);
  const signer = new ethers.Wallet(PRIVATE_KEY, provider);

  const contractABI = require('./abis/Temp.json');
  const contract = new ethers.Contract(CONTRACT_ADDRESS, contractABI, signer);

  async function fetchFileFromIPFS() {
    try {
      console.log("here");
      const userData = await contract.getUserData(userAddress);
      console.log(userData);
      const stream = client.cat(userData);
      let data = [];

      for await (const chunk of stream) {
        data.push(chunk);
      }

      const blob = new Blob(data, { type: 'image/jpeg' });
      const url = URL.createObjectURL(blob);
      setFileUrl(url);
    } catch (error) {
      console.error('Error fetching file from IPFS:', error);
    }
  }

  return (
    <div>
      <input
        type="text"
        placeholder="Your Address"
        onChange={(e) => setUserAddress(e.target.value)}
      />
      
      <button onClick={fetchFileFromIPFS}>Fetch File from IPFS</button>
      {fileUrl && <a href={fileUrl} download>Download File</a>}
    </div>
  );
}

export default ViewComponent;
