import React, { useRef, useState } from 'react';
import { ethers } from 'ethers';
import { create } from 'ipfs-http-client';

const client = create({
  host: '127.0.0.1',
  port: 5001,
  protocol: 'http',
});

function UploadFileComponent() {
  const fileInput = useRef(null);
  const [fileCid, setFileCid] = useState(null);
  const [userAddress, setUserAddress] = useState('');
  const [recipientAddress, setRecipientAddress] = useState('');

  const CONTRACT_ADDRESS = '0xA88a38133D38A56f2c9D552eD8b8B88e8dAbd8fa';
  const PRIVATE_KEY = 'ffce87da659a88c5ad63d447fff3be4308b00845bf002861ba91ad0ce5994603';
  const API_KEY = 'rdO0i4o--THH81mB5V3Cb9SE1UiwCtrh';

  async function handleUploadToIPFS() {
    const file = fileInput.current.files[0];
    if (!file) {
      console.log('No file selected');
      return;
    }

    try {
      const reader = new FileReader();
      reader.onloadend = async () => {
        const { cid } = await client.add(reader.result);
        setFileCid(cid.toString());
      };
      reader.readAsArrayBuffer(file);
      console.log(cid);
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  }

  const handleSendToContract = async () => {
    try {
      const network = 'rinkeby';
      const provider = new ethers.providers.AlchemyProvider(network, API_KEY);
      const signer = new ethers.Wallet(PRIVATE_KEY, provider);
  
      const contractABI = require('./abis/Temp.json');
      const contract = new ethers.Contract(CONTRACT_ADDRESS, contractABI, signer);
  
      const transaction = await contract.setUserData(fileCid, recipientAddress);
      await transaction.wait();
      console.log('Transaction successful');
    } catch (error) {
      console.error('Error sending to contract:', error);
    }
  };

  return (
    <div>
      <input type="file" ref={fileInput} />
      <button onClick={handleUploadToIPFS}>Upload to IPFS</button>
      <input
        type="text"
        placeholder="Your Address"
        onChange={(e) => setUserAddress(e.target.value)}
      />
      <input
        type="text"
        placeholder="Recipient Address"
        onChange={(e) => setRecipientAddress(e.target.value)}
      />
      <button onClick={handleSendToContract}>Send to Contract</button>
    </div>
  );
}

export default UploadFileComponent;
